import "../assets/scss/discover.scss";

import React, { Component } from "react";

var stopScroll = true;

export default class Discover2 extends React.Component {
  constructor(props) {
    super(props);

    this.scrollingContainer = this.scrollingContainer.bind(this);
    this.state = {
      opacity: null,
      tappedOpacity: null,
      isTransitionOver: null,
      isAnimationOver: null,
      scanningOpacity: 0,
      redGridOpacity: 0,
      threeCharsOpacity: null,
      dotsOpacity: null,
      character1Opacity: null,
      navbarOpacity: 0,
      lowerOpacity: 0,
      cancelOpacity: null,
      character2Opacity: null,
      shareOpacity: null,
      canScrollDown: null,
      scrollInviteOpacity: 1
    };
  }
  componentDidMount() {
    document.body.className = "first-component-body";
  }

  componentDidUpdate() {
    if (this.state.canScrollDown) {
      document.body.className = "first-component-body-canScroll";
    }
  }
  scrollingContainer() {
    var firstText = document.querySelector("#firstText");
    var secondText = document.querySelector("#secondText");
    var thirdText = document.querySelector("#thirdText");
    var fourthText = document.querySelector("#fourthText");
    var firstTop = firstText.getBoundingClientRect().top;
    var secondTop = secondText.getBoundingClientRect().top;
    var thirdTop = thirdText.getBoundingClientRect().top;
    var fourthTop = fourthText.getBoundingClientRect().top;

    if (firstTop < 0) {
      this.setState({
        scanningOpacity: 0,
        redGridOpacity: 0,
        threeCharsOpacity: 1,
        dotsOpacity: 1,
        scrollInviteOpacity: 0
      });
    } else if (firstTop > 0) {
      this.setState({
        threeCharsOpacity: 0,
        dotsOpacity: 0,
        scanningOpacity: 1,
        redGridOpacity: 1
      });
    }

    if (secondTop < 0) {
      this.setState({
        threeCharsOpacity: 0,
        dotsOpacity: 0,
        character1Opacity: 1
      });
    } else if (secondTop > 0 && secondTop < 500) {
      this.setState({
        character1Opacity: 0,
        threeCharsOpacity: 1,
        dotsOpacity: 1
      });
    }

    if (thirdTop < 0) {
      this.setState({
        character1Opacity: 0,
        navbarOpacity: 0,
        lowerOpacity: 0,
        cancelOpacity: 1,
        character2Opacity: 1,
        shareOpacity: 1
      });
    } else if (thirdTop > 0 && thirdTop < 500) {
      this.setState({
        cancelOpacity: 0,
        character2Opacity: 0,
        shareOpacity: 0,
        character1Opacity: 1,
        navbarOpacity: 1,
        lowerOpacity: 1
      });
    }

    if (fourthTop > 317) {
      this.setState({
        canScrollDown: true
      });
    }
  }

  render() {
    return <div></div>;
  }
}
