import "../assets/scss/discover.scss";

import React, { Component } from "react";

var stopScroll = true;

export default class Discover2 extends React.Component {
  constructor(props) {
    super(props);

    this.scrollingContainer = this.scrollingContainer.bind(this);
    this.state = {
      opacity: null,
      tappedOpacity: null,
      isTransitionOver: null,
      screen1Opacity: 0,
      screen2Opacity: 1,
      screen3Opacity: 0
    };
  }
  componentDidMount() {
    document.body.className = "first-component-body";
  }

  componentDidUpdate() {
    if (this.state.canScrollDown) {
      document.body.className = "first-component-body-canScroll";
    }
  }
  scrollingContainer() {
    var firstText = document.querySelector("#firstText");
    var secondText = document.querySelector("#secondText");
    var thirdText = document.querySelector("#thirdText");
    var fourthText = document.querySelector("#fourthText");
    var firstTop = firstText.getBoundingClientRect().top;
    var secondTop = secondText.getBoundingClientRect().top;
    var thirdTop = thirdText.getBoundingClientRect().top;
    var fourthTop = fourthText.getBoundingClientRect().top;

    if (firstTop < 0) {
      this.setState({
        scanningOpacity: 0,
        redGridOpacity: 0,
        threeCharsOpacity: 1,
        dotsOpacity: 1,
        scrollInviteOpacity: 0
      });
    } else if (firstTop > 0) {
      this.setState({
        threeCharsOpacity: 0,
        dotsOpacity: 0,
        scanningOpacity: 1,
        redGridOpacity: 1
      });
    }

    if (secondTop < 0) {
      this.setState({
        threeCharsOpacity: 0,
        dotsOpacity: 0,
        character1Opacity: 1
      });
    } else if (secondTop > 0 && secondTop < 500) {
      this.setState({
        character1Opacity: 0,
        threeCharsOpacity: 1,
        dotsOpacity: 1
      });
    }

    if (thirdTop < 0) {
      this.setState({
        character1Opacity: 0,
        navbarOpacity: 0,
        lowerOpacity: 0,
        cancelOpacity: 1,
        character2Opacity: 1,
        shareOpacity: 1
      });
    } else if (thirdTop > 0 && thirdTop < 500) {
      this.setState({
        cancelOpacity: 0,
        character2Opacity: 0,
        shareOpacity: 0,
        character1Opacity: 1,
        navbarOpacity: 1,
        lowerOpacity: 1
      });
    }

    if (fourthTop > 317) {
      this.setState({
        canScrollDown: true
      });
    }
  }

  render() {
    return (
      <section className="section" id="discover">
        <div
          className="scrollable"
          id="#scrollable"
          onScroll={this.scrollingContainer}
        >
          <div className="left">
            <div className="content">
              <div className="text" id="firstText">
                <h1>UniU</h1>
                <br />
                <p>
                  Un'app per gestire al meglio il tuo percorso universitario
                </p>
              </div>
              <div className="content-inner"></div>
            </div>
            <div className="content">
              <div className="text" id="secondText">
                <p>Monitora la tua carriera universitaria</p>
              </div>
              <div className="content-inner"></div>
            </div>
            <div className="content">
              <div className="text" id="thirdText">
                <h1></h1>
                <p>
                  Salva il tuo orario e ricevi una notifica quando Ã¨ il momento
                  di cambiare aula
                </p>
              </div>
            </div>
            <div className="content">
              <div className="text" id="fourthText">
                <h1></h1>
                <p>
                  Fai previsioni sulla tua media in base all'esito dei tuoi
                  prossimi esami
                </p>
              </div>
            </div>
          </div>
          {/* ////////////////





          ////////////// */}
          <div className="phone">
            <img
              style={{ opacity: this.state.screen1Opacity }}
              alt="App screen"
              className="screen"
              src={require("../assets/img/screen1.png")}
            />

            {/* elements that will change on scroll */}
            <img
              style={{ opacity: this.state.screen2Opacity }}
              alt="App screen"
              className="screen2"
              src={require("../assets/img/screen2.png")}
            />

            {/* second set of elements */}
            <img
              style={{ opacity: this.state.screen3Opacity }}
              alt="App screen"
              className="screen3"
              src={require("../assets/img/screen3.png")}
            />
          </div>
        </div>
      </section>
    );
  }
}
