import "../assets/scss/discover.scss";

import React, { Component } from "react";

var stopScroll = true;

export default class Discover2 extends React.Component {
  constructor(props) {
    super(props);

    this.scrollingContainer = this.scrollingContainer.bind(this);
    this.state = {
      opacity: null,
      tappedOpacity: null,
      isTransitionOver: null,
      isAnimationOver: null,
      scanningOpacity: 0,
      redGridOpacity: 0,
      threeCharsOpacity: null,
      dotsOpacity: null,
      character1Opacity: null,
      navbarOpacity: 0,
      lowerOpacity: 0,
      cancelOpacity: null,
      character2Opacity: null,
      shareOpacity: null,
      canScrollDown: null,
      scrollInviteOpacity: 1
    };
  }
  componentDidMount() {
    document.body.className = "first-component-body";
  }

  componentDidUpdate() {
    if (this.state.canScrollDown) {
      document.body.className = "first-component-body-canScroll";
    }
  }
  scrollingContainer() {
    var firstText = document.querySelector("#firstText");
    var secondText = document.querySelector("#secondText");
    var thirdText = document.querySelector("#thirdText");
    var fourthText = document.querySelector("#fourthText");
    var firstTop = firstText.getBoundingClientRect().top;
    var secondTop = secondText.getBoundingClientRect().top;
    var thirdTop = thirdText.getBoundingClientRect().top;
    var fourthTop = fourthText.getBoundingClientRect().top;

    if (firstTop < 0) {
      this.setState({
        scanningOpacity: 0,
        redGridOpacity: 0,
        threeCharsOpacity: 1,
        dotsOpacity: 1,
        scrollInviteOpacity: 0
      });
    } else if (firstTop > 0) {
      this.setState({
        threeCharsOpacity: 0,
        dotsOpacity: 0,
        scanningOpacity: 1,
        redGridOpacity: 1
      });
    }

    if (secondTop < 0) {
      this.setState({
        threeCharsOpacity: 0,
        dotsOpacity: 0,
        character1Opacity: 1
      });
    } else if (secondTop > 0 && secondTop < 500) {
      this.setState({
        character1Opacity: 0,
        threeCharsOpacity: 1,
        dotsOpacity: 1
      });
    }

    if (thirdTop < 0) {
      this.setState({
        character1Opacity: 0,
        navbarOpacity: 0,
        lowerOpacity: 0,
        cancelOpacity: 1,
        character2Opacity: 1,
        shareOpacity: 1
      });
    } else if (thirdTop > 0 && thirdTop < 500) {
      this.setState({
        cancelOpacity: 0,
        character2Opacity: 0,
        shareOpacity: 0,
        character1Opacity: 1,
        navbarOpacity: 1,
        lowerOpacity: 1
      });
    }

    if (fourthTop > 317) {
      this.setState({
        canScrollDown: true
      });
    }
  }

  render() {
    return (
      <section className="screens_first_section desktop_only" id="discover">
        <div className="banner_image">
          <div
            className={
              this.state.isTransitionOver ? "panel_up_animation" : "panel_up"
            }
            onTransitionEnd={this.panelsOver}
          ></div>

          <div
            className={
              this.state.isTransitionOver
                ? "panel_down_animation"
                : "panel_down"
            }
          ></div>
        </div>
        <div
          className={
            this.state.isAnimationOver ? "scrollable_visible" : "scrollable"
          }
          id="#scrollable"
          onScroll={this.scrollingContainer}
        >
          <div className="left">
            <div className="content">
              <div className="text" id="firstText">
                <img
                  className="sx1_title"
                  alt="Bosch AR experiment"
                  style={{ opacity: this.state.firstOpacity }}
                  src={require("../../assets/images/screens_sx1_title.svg")}
                />
                <br />
                <img
                  className="sx1"
                  alt="Bring Bosch's art out of the canvas"
                  style={{ opacity: this.state.firstOpacity }}
                  src={require("../../assets/images/screens_sx1.svg")}
                />
              </div>
              <div className="content-inner"></div>
            </div>
            <div className="content">
              <div className="text">
                <h1> </h1>
              </div>
              <div className="content-inner"></div>
            </div>
            <div className="content">
              <div className="text" id="thirdText">
                <h1></h1>
                <p className="sx3">
                  Interact with the characters
                  <br />
                  by changing size, posizion <br />
                  and orientation
                </p>
              </div>
            </div>
          </div>
          <div className="right">
            <div className="content">
              <div className="text">
                <h1> </h1>
                <p className="dx1">
                  Scan the environment
                  <br />
                  around you
                </p>
              </div>
              <div className="content-inner"></div>
            </div>
            <div className="content">
              <div className="text" id="secondText">
                <h1></h1>
                <p className="dx2">
                  Choose between 5 different
                  <br />
